						["custom"] = "function(allstates, event, unit, ...)\n    --iterates through visible nameplates and checks for specified raidicon.  \n    if event == \"RAID_TARGET_UPDATE\"\n    and aura_env.config.enableIcon then \n        for i = 1, 40 do\n            local u = \"nameplate\"..i\n            if UnitExists(u) then \n                local guid = UnitGUID(u)\n                if guid\n                and not allstates[guid]\n                and aura_env.npcIDs[select(6,strsplit(\"-\", guid))]\n                and aura_env.config.specifiedIcon[GetRaidTargetIndex(u)]\n                then\n                    allstates[guid] = {\n                        show = true,\n                        changed = true,\n                        name = aura_env.counter[guid] or 1,\n                        PassUnit = u,\n                    }\n                    --sound only fired if nameplate is visible the moment that icon is applied, only relevant for VERY first interrupt.\n                    if aura_env.config.enableSound\n                    and allstates[guid].name == aura_env.config.enableSpecified\n                    then\n                        PlaySoundFile(aura_env.sound[aura_env.config.specifiedSound], \"MASTER\")\n                    end\n                end\n            end\n        end\n        return true\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID, spellName, spellschool, extraspellID, extraspellName  = ...\n        if subEvent == \"SPELL_CAST_SUCCESS\"\n        and aura_env.spellIDs[tostring(spellID)] then\n            local state = allstates[sourceGUID]\n            --cancels text aura if counter matches your specified value\n            if aura_env.config.showText\n            and aura_env.caststart[sourceGUID]\n            and aura_env.caststart[sourceGUID] == aura_env.config.enableSpecified\n            then\n                WeakAuras.ScanEvents(\"HIDE_TEXTAURA\", \"nilcheck\")\n            end\n            --count & print failed interrupts in chat (if option is actived)\n            if aura_env.counter[sourceGUID] then\n                aura_env.counter[sourceGUID] = aura_env.counter[sourceGUID] + 1\n                if aura_env.config.printfails\n                and sourceRaidFlags\n                and sourceRaidFlags > 0\n                then\n                    print((aura_env.counter[sourceGUID]-1)..\". interrupt failed on \"..aura_env.sendemote[sourceRaidFlags])\n                end\n            else aura_env.counter[sourceGUID] = 2 \n                if aura_env.config.printfails\n                and sourceRaidFlags\n                and sourceRaidFlags > 0\n                then\n                    print((aura_env.counter[sourceGUID]-1)..\". interrupt failed on \"..aura_env.sendemote[sourceRaidFlags])\n                end\n            end\n            --update state\n            if state then\n                state.changed = true\n                state.name = aura_env.counter[sourceGUID]\n            end\n            --check if current count == limit and reset to 1 & update TSU\n            local limit = aura_env.config.limit\n            if aura_env.counter[sourceGUID] == (limit+1) and limit > 0 then\n                aura_env.counter[sourceGUID] = 1\n                if state then\n                    state.changed = true\n                    state.name = aura_env.counter[sourceGUID]\n                end\n            end\n            --fire sound if it matches your specified value\n            if aura_env.config.enableSound\n            and aura_env.counter[sourceGUID] \n            and aura_env.counter[sourceGUID] == aura_env.config.enableSpecified\n            then\n                PlaySoundFile(aura_env.sound[aura_env.config.specifiedSound], \"MASTER\")\n            end\n            return true\n        elseif subEvent == \"SPELL_INTERRUPT\"\n        and aura_env.spellIDs[tostring(extraspellID)] then\n            local state = allstates[destGUID]\n            --cancels text aura if counter matches your specified value\n            if aura_env.config.showText\n            and aura_env.caststart[destGUID]\n            and aura_env.caststart[destGUID] == aura_env.config.enableSpecified\n            then\n                WeakAuras.ScanEvents(\"HIDE_TEXTAURA\", \"nilcheck\")    \n            end\n            --increase count\n            if aura_env.counter[destGUID] then\n                aura_env.counter[destGUID] = aura_env.counter[destGUID] + 1\n            else\n                aura_env.counter[destGUID] = 2 \n            end\n            --update state with new count\n            if state then\n                state.changed = true\n                state.name = aura_env.counter[destGUID]\n            end\n            --check if current count == limit and reset to 1 & update TSU\n            local limit = aura_env.config.limit\n            if aura_env.counter[destGUID] == (limit+1) and limit > 0 then\n                aura_env.counter[destGUID] = 1\n                if state then\n                    state.changed = true\n                    state.name = aura_env.counter[destGUID]\n                end\n            end\n            -- fire sound if it matches your specified value\n            if aura_env.config.enableSound\n            and aura_env.counter[destGUID]\n            and aura_env.counter[destGUID] == aura_env.config.enableSpecified\n            and (aura_env.config.enableIcon and sourceRaidFlags and aura_env.config.specifiedIcon[aura_env.translate[destRaidFlags]]\n                or not aura_env.config.enableIcon\n            )\n            then\n                PlaySoundFile(aura_env.sound[aura_env.config.specifiedSound], \"MASTER\")   \n            end\n            return true\n        elseif subEvent == \"SPELL_CAST_START\"\n        and aura_env.spellIDs[tostring(spellID)] then \n            if (\n                aura_env.config.specifiedIcon\n                and aura_env.config.enableIcon\n                and sourceRaidFlags \n                and (\n                    aura_env.config.specifiedIcon[aura_env.translate[sourceRaidFlags]]\n                    or sourceRaidFlags == 0 and not aura_env.caststart[sourceGUID]\n                )\n            )\n            or not aura_env.config.enableIcon\n            then\n                if aura_env.caststart[sourceGUID] then\n                    aura_env.caststart[sourceGUID] = aura_env.caststart[sourceGUID] + 1\n                else \n                    aura_env.caststart[sourceGUID] = 1\n                end\n                \n                --reset cast start counter if counter = limit\n                local limit = aura_env.config.limit\n                if aura_env.caststart[sourceGUID] == (limit + 1) and limit > 0 then\n                    aura_env.caststart[sourceGUID] = 1\n                end\n                --fire cast start sound if it matches your specified value + raidicon\n                if aura_env.config.enableCastSound\n                and aura_env.caststart[sourceGUID]\n                and aura_env.caststart[sourceGUID] == aura_env.config.enableSpecified\n                then\n                    PlaySoundFile(aura_env.castsound[aura_env.config.specifiedCastSound], \"MASTER\")\n                end\n                --starts text aura if it matches your specified value\n                if aura_env.config.showText\n                and aura_env.caststart[sourceGUID]\n                and aura_env.caststart[sourceGUID] == aura_env.config.enableSpecified\n                then\n                    WeakAuras.ScanEvents(\"SHOW_TEXTAURA\", \"nilcheck\")\n                end\n            end\n            --hide anchor-to-nameplate when npc dies\n        elseif subEvent == \"UNIT_DIED\" then\n            local state = allstates[destGUID] \n            if state then\n                state.show = false\n                state.changed = true\n                return true\n            end\n        end\n    elseif event == \"NAME_PLATE_UNIT_ADDED\" and unit then\n        local guid = UnitGUID(unit)\n        if guid then\n            local state = allstates[guid]\n            --update state with current count when nameplate shows\n            if state then\n                state.changed = true\n                state.PassUnit = unit\n                state.name = aura_env.counter[guid] or 1\n                return true\n            else\n                if aura_env.npcIDs[select(6,strsplit(\"-\", guid))] \n                and (\n                    (\n                        aura_env.config.enableIcon\n                        and aura_env.config.specifiedIcon[GetRaidTargetIndex(unit)]\n                    )\n                    or not aura_env.config.enableIcon\n                )\n                then\n                    allstates[guid] = {\n                        PassUnit = unit,\n                        show = true,\n                        name = aura_env.counter[guid] or 1,\n                        changed = true,\n                    }\n                    return true\n                end\n            end\n            --create state when nameplate shows          \n        end\n    elseif event == \"NAME_PLATE_UNIT_REMOVED\" and unit then\n        local guid = UnitGUID(unit)\n        if guid then\n            local state = allstates[guid]\n            if state then\n                state.changed = true\n                state.PassUnit = \"none\"\n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.npcIDs = {}\n        aura_env.spellIDs = {}\n        for match in aura_env.config.npcID:gmatch(\"%d+\") do aura_env.npcIDs[match] = true end\n        for match in aura_env.config.spellID:gmatch(\"%d+\") do aura_env.spellIDs[match] = true end\n        aura_env.counter = {}\n        aura_env.caststart = {}\n        if IsAddOnLoaded(\"ExRT\") \n        and _G.VExRT.Note.Text1 \n        and aura_env.config.overrideSettings then\n            local text = _G.VExRT.Note.Text1\n            --counts lines except gaps\n            local betweenLine = false\n            local myName = UnitName(\"player\")\n            for line in text:gmatch('[^\\r\\n]+') do\n                --searches for \"endLine\" in Note\n                if line:match(\"endLine\") then\n                    betweenLine = false\n                end\n                if betweenLine then\n                    --checks if your name is found inbetween startLine and endLine\n                    if line:match(myName) then\n                        local mark = line:match(\"^{([^}]+)}\")\n                        if mark and aura_env.translateERT[mark] then\n                            local turn = 0\n                            local backupsList = {}\n                            for backups in line:gmatch(\"%(([^)]*)%)\") do\n                                local isbackup = false\n                                for name in backups:gmatch(\"|c%x%x%x%x%x%x%x%x([^|]+)|\") do\n                                    if isbackup then\n                                        backupsList[name] = true\n                                    end\n                                    isbackup = true\n                                end \n                            end\n                            for name in line:gmatch(\"|c%x%x%x%x%x%x%x%x([^|]+)|\") do\n                                if not backupsList[name] then\n                                    turn = turn + 1\n                                end\n                                if name == myName then\n                                    aura_env.config.enableSpecified = turn\n                                    aura_env.config.specifiedIcon[aura_env.translateERT[mark]] = true\n                                    --aura_env.config.enableSound = true --forces people to use a sound (disabled by default)\n                                end\n                            end \n                            aura_env.config.limit = turn\n                        end\n                    end\n                end\n                --searches for \"startLine\" in Note\n                if line:match(\"startLine\") then\n                    betweenLine = true\n                    --disable all currently selected raidicons & enable raidicon from note\n                    aura_env.config.enableIcon = true\n                    for i = 1,8 do\n                        aura_env.config.specifiedIcon[i] = false\n                    end\n                end\n            end\n        end\n        for i = 1, 20 do\n            --weakaura used to lack something to anchor interruptorder to when units are present on pull\n            local unit = \"nameplate\"..i\n            local guid = UnitGUID(unit)\n            local icon = GetRaidTargetIndex(unit)\n            if guid\n            and aura_env.npcIDs[select(6, strsplit(\"-\", guid))]\n            and (\n                aura_env.config.enableIcon\n                and icon\n                and aura_env.config.specifiedIcon[icon]\n                or not aura_env.config.enableIcon\n            )\n            then\n                allstates[guid] = {\n                    show = true,\n                    changed = true,\n                    name = aura_env.counter[guid] or 1,\n                    PassUnit = unit,\n                }\n            end\n        end\n        return true\n    end    \nend",
