ue --forces people to use a sound (disabled by default)\n            end\n        end\n    end\n    if event == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" then\n        for i = 1,5 do\n            --weakaura used to lack something to anchor interruptorder to when targets are present on pull\n            local unit = \"boss\"..i\n            local guid = UnitGUID(unit)\n            if guid and aura_env.npcIDs[select(6, strsplit(\"-\", guid))] then\n                allstates[guid] = {\n                    show = true,\n                    changed = true,\n                    name = aura_env.counter[guid] or 1,\n                    PassUnit = unit,\n                }\n            end\n        end\n    end\n    return true    \nend",
