				["init"] = {
					["custom"] = "aura_env.shorten = function(unit, num)\n    return GetClassColoredTextForUnit(unit, WA_Utf8Sub(UnitName(unit), num))\nend\n\naura_env.roleicon = function(name)\n    local role = UnitGroupRolesAssigned(name)\n    if role ~= \"NONE\" then\n        return CreateTextureMarkup([[Interface\\LFGFrame\\UI-LFG-ICON-ROLES]], 256, 256, 0, 0, GetTexCoordsForRole(UnitGroupRolesAssigned(name)))\n    else\n        return \"\"\n    end\nend\n\naura_env.spells = {\n    [348064] = true,\n    [358705] = true,\n    \n}\n\n\naura_env.offset = function()\n    local count = 0\n    local h = aura_env.config[\"horizontal\"]\n    local v = aura_env.config[\"vertical\"]\n    local xgap = aura_env.config[\"xgap\"]\n    local ygap = aura_env.config[\"ygap\"]\n    \n    for i, region in pairs(WeakAuras.clones[aura_env.id]) do\n        local xOffset = 0\n        local yOffset = 0\n        if h ~= 1 then\n            if h == 2 then\n                xOffset = 0 - (region.width + xgap) * (region.state.index-1)\n            elseif h == 3 then\n                xOffset = 0 + (region.width + xgap) * (region.state.index-1)\n            end\n        end\n        \n        if v ~= 1 then\n            if v == 2 then\n                yOffset = 0 + (region.height + ygap) * (region.state.index-1)\n            elseif v == 3 then\n                yOffset = 0 - (region.height + ygap) * (region.state.index-1)\n            end\n        end\n        \n        region:SetAnchor(\"CENTER\" , WeakAuras.regions[aura_env.id].region, \"CENTER\")\n        region:SetOffset(xOffset, yOffset)\n        \n        count = count + 1\n    end\nend\n\n-- aura_env.offset in On Show/Hide",
