n string.sub(name, 1, length-3)\n    else\n        return name\n    end\nend\n\n\naura_env.ERT = function()\n    local text = _G.VExRT.Note.Text1\n    aura_env.assign = false\n    for line in string.gmatch(text,'[^\\r\\n]+') do\n        local start = strsplit(\" \", line)\n        if string.lower(start) == \"end\" then\n            aura_env.assign = false\n            aura_env.assign2 = false\n        elseif string.lower(start) == \"backup\" then\n            aura_env.assign = false\n            aura_env.assign2 = true\n        elseif aura_env.assign2 then\n            aura_env.backup[aura_env.countb] = aura_env.colorcode(start)\n            aura_env.countb = aura_env.countb+1\n        elseif aura_env.assign then\n            aura_env.players[aura_env.count] = aura_env.colorcode(start)\n            aura_env.count = aura_env.count+1\n            \n            \n        elseif string.lower(start) == \"start\" then\n            aura_env.players = {}\n            aura_env.backup = {}\n            aura_env.num = 0\n            aura_env.alert = 1\n            aura_env.count = 1\n            aura_env.countb = 1\n            aura_env.assign = true\n            aura_env.assign2 = false\n        end\n    end\nend",
