					["custom"] = "aura_env.active = false\naura_env.yells = {\n    [\"Agatha\"] = {\"Massacre\", select(3, GetSpellInfo(355551))},\n    [\"Arthura\"] = {\"Stack Debuff\", select(3, GetSpellInfo(350039))},\n    [\"Aradne\"] = {\"Soak Zones\", select(3, GetSpellInfo(350098))},\n    [\"Annhylde\"] = {\"Shield Zone\", select(3, GetSpellInfo(350158))},\n    [\"Daschla\"] = {\"Big Circles\", select(3, GetSpellInfo(350184))},\n    [\"Brynja\"] = {\"Small Circles\", select(3, GetSpellInfo(350109))},\n}\n\naura_env.offset = function()\n    local count = 0\n    local h = aura_env.config[\"horizontal\"]\n    local v = aura_env.config[\"vertical\"]\n    local xgap = aura_env.config[\"xgap\"]\n    local ygap = aura_env.config[\"ygap\"]\n    \n    for i, region in pairs(WeakAuras.clones[aura_env.id]) do\n        local xOffset = 0\n        local yOffset = 0\n        if h ~= 1 then\n            if h == 2 then\n                xOffset = 0 - (region.width + xgap) * count\n            elseif h == 3 then\n                xOffset = 0 + (region.width + xgap) * count\n            end\n        end\n        \n        if v ~= 1 then\n            if v == 2 then\n                yOffset = 0 + (region.height + ygap) * count\n            elseif v == 3 then\n                yOffset = 0 - (region.height + ygap) * count\n            end\n        end\n        \n        region:SetAnchor(\"CENTER\" , WeakAuras.regions[aura_env.id].region, \"CENTER\")\n        region:SetOffset(xOffset, yOffset)\n        \n        count = count + 1\n    end\nend\n\n-- aura_env.offset in On Show/Hide",
