						["custom"] = "function(s, e, ...)\n    local timestamp, subevent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID = ... -- full CLEU\n    if spellID == aura_env.spell  then\n        if subevent == \"SPELL_CAST_START\" then\n            aura_env.ind = 1\n            \n        elseif subevent == \"SPELL_AURA_APPLIED\" then\n            aura_env.ind = aura_env.ind or 1\n            if UnitInRaid(destName) then\n                local dur, expires = select(5, WA_GetUnitAura(destName, spellID, aura_env.debug))\n                s[destGUID] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = dur,\n                    name = aura_env.roleicon(destName)..\" \"..aura_env.shorten(destName, 8),\n                    expirationTime = expires,\n                    autoHide = true,\n                    index = aura_env.ind+1\n                }\n                aura_env.ind = aura_env.ind+1\n                \n            else\n                local dur, expires = select(5, WA_GetUnitAura(destName, spellID, aura_env.debug))\n                s[destGUID] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    name = \"Rattle-Cast\",\n                    duration = dur,\n                    expirationTime = expires,\n                    autoHide = true,\n                    index = 1,\n                }\n            end\n            return true\n            \n        elseif s[destGUID] then\n            s[destGUID].show = false\n            s[destGUID].changed = true\n            return true\n            \n        end\n    end\nend\n\n\n\n\n\n",
