						["custom"] = "function(s,e)\n  \n  if e == \"CHALLENGE_MODE_START\" and  aura_env.config.warn and C_ChallengeMode.IsChallengeModeActive() and (not aura_env.recent or GetTime() - aura_env.recent > 60) then\n    aura_env.recent = GetTime()\n    aura_env.printReminder()\n  else\n    if not PlayerChoiceFrame then return end\n    -- on close\n    if not PlayerChoiceFrame:IsShown() then\n      if s[\"\"] and (s[\"\"].type == \"manual\" or s[\"\"].type == \"interact\") then\n        s[\"\"].show = false\n        s[\"\"].changed = true\n        return true\n      end\n      --spam prevention\n    elseif s[\"\"] and s[\"\"].type ~= \"selected\" then\n      return\n      \n    else --carry on\n      local role = UnitGroupRolesAssigned(\"player\")\n      if role == \"NONE\" then\n        role = GetSpecializationRole(GetSpecialization())\n      end\n      \n      local data = C_PlayerChoice.GetCurrentPlayerChoiceInfo()\n      if data and role and role ~= 0 then \n        local boss, selection = aura_env.getBossSelection(data, role)\n        \n        -- if set to manual for this boss\n        if selection == 4 then\n          local name = \"Manual selection\"\n          s[\"\"] = {\n            type = \"manual\",\n            show = true,\n            changed = true,\n            name = name,\n          }\n          return true\n          \n          -- otherwise see what spell is selected for the boss then iterate the options to find a match\n        else\n          local selectedSpell = aura_env.powers[role][boss][selection]\n          if not selectedSpell then return end\n          for i, choice in ipairs(data.options) do\n            if (choice.spellID == selectedSpell) then\n              -- select the option\n              if InCombatLockdown() then\n                \n                local optionFrames = PlayerChoiceFrame:GetLayoutChildren()\n                local flashFrame = optionFrames[i]\n                if flashFrame then\n                  UIFrameFlash(flashFrame, 0.2,0.2,1,true,0,0,nil)\n                end\n                local name = \"Click \"..(GetSpellInfo(choice.spellID))\n                s[\"\"] = {\n                  type = \"interact\",\n                  show = true,\n                  changed = true,\n                  icon = GetSpellTexture(choice.spellID),\n                  name = name,\n                }\n              else\n                local name = \"Selected: \"..(GetSpellInfo(choice.spellID))\n                C_PlayerChoice.SendPlayerChoiceResponse(choice.buttons[1].id)\n                \n                -- then announce if selected and output text to the user\n                if aura_env.config.announcer and UnitInParty(\"player\") then\n                  local announceString = \"Picked Power: \"..GetSpellLink(choice.spellID)\n                  SendChatMessage(announceString ,\"party\");\n                end\n                -- and show text on screen for it\n                s[\"\"] = {\n                  type = \"selected\",\n                  show = true,\n                  changed = true,\n                  duration = aura_env.config.dur, \n                  expirationTime = GetTime() + aura_env.config.dur,\n                  progressType = \"timed\",\n                  autoHide = true, \n                  icon = GetSpellTexture(choice.spellID),\n                  name = name,\n                }\n                HideUIPanel(PlayerChoiceFrame)\n                return true\n              end\n            end \n          end \n        end\n      end\n    end\n  end\nend",
