utput2, aura_env.bossNames[boss])\n        else\n          local spell = aura_env.powers[role][boss][bossT[role]]\n          local link = GetSpellLink(spell)\n          local icon = GetSpellTexture(spell)\n          icon = (\"|T%d:0|t\"):format(icon)\n          output2 = (\"%s\\n %s = %s%s\"):format(output2, aura_env.bossNames[boss], icon, link)\n        end\n      end\n    end\n    print(\"|cffff9f40M+ Tormented AutoPicker Reminder!|r\")\n    print(output1)\n    print(output2)\n  end\nend\n\naura_env.getBossSelection = function(data, role)\n  for i, choice in ipairs(data.options) do\n    local boss = aura_env.uniqueSpells[role][choice.spellID]\n    if boss then\n      return boss, aura_env.config[boss][role]\n    end\n  end\nend\n\n\n",
